create table tb_business_sector (id int8 generated by default as identity, sector varchar(255), id_company int8, primary key (id));
create table tb_candidate (id int8 generated by default as identity, cpf varchar(255), email varchar(255), last_name varchar(255), name varchar(255), occupation varchar(255), phone varchar(255), whatsapp varchar(255), id_city int8, primary key (id));
create table tb_city (id int8 generated by default as identity, name varchar(255), id_state int8, primary key (id));
create table tb_company (id int8 generated by default as identity, about varchar(255), activated boolean not null, activation_email_sended boolean not null, cnpj varchar(255), desktop_notification boolean not null, email varchar(255), email_notification boolean not null, first_vacancy boolean not null, name varchar(255), password varchar(255), phone varchar(255), whatsapp_notification boolean not null, id_business_sector int8, id_city int8, primary key (id));
create table tb_state (id int8 generated by default as identity, name varchar(255), primary key (id));
create table tb_type_job (id int8 generated by default as identity, type varchar(255), primary key (id));
create table tb_type_of_pay (id int8 generated by default as identity, type varchar(255), primary key (id));
create table tb_type_of_workload (id int8 generated by default as identity, type varchar(255), primary key (id));
create table tb_vacancy (id int8 generated by default as identity, benefits varchar(3000), date_of_activation varchar(255), description varchar(3000), disabled_people boolean not null, for_students boolean not null, immediate_hiring boolean not null, name varchar(255), need_experience boolean not null, salary varchar(255), salary_to_negotiate boolean not null, vacancy_status int4, work_schedule varchar(255), id_city int8, id_company int8, id_type_job int8, id_type_of_pay int8, id_type_of_workload int8, primary key (id));
create table tb_vacancy_candidate (id int8 generated by default as identity, cpf_candidate varchar(255), id_vacancy int8, primary key (id));
